function load() {
  const dataBuffer = fs.readFileSync(contentPath, "utf8");
  return dataBuffer;
}

function save(content) {
  const string = JSON.stringify(content);
  return fs.writeFileSync(contentPath, string);
}

export default {
  save,
  load,
}
