import React from 'react';
import { connect } from 'react-redux';

import { changeSignal } from './redux/actionCreators.js';

import redSignal from './images/redSignal.jpeg';
import greenSignal from './images/greenSignal.jpeg';
import yellowSignal from './images/yellowSignal.jpeg';

const renderSignal = (signalColor) => {
  if (signalColor === 'red') {
    return redSignal;
  }
  if (signalColor === 'green') {
    return greenSignal;
  }
  if (signalColor === 'yellow') {
    return yellowSignal;
  }
  return null;
};

class TrafficSignal extends React.Component {
  render() {

    return (
      <div>
        <div className="button-container">
          <button type="button" onClick={(event) => this.props.change('red')}>Red</button>
          <button type="button" onClick={(event) => this.props.change('yellow')}>Yellow</button>
          <button type="button" onClick={(event) => this.props.change('green')}>Green</button>
        </div>
        <img className="signal" src={renderSignal(this.props.signalColor)} alt="" />
      </div>
    )
  }
}

const mapStateToProps = state => ({                                                           
  signalColor: state.reducer,
});

const mapDispatchToProps = dispatch => ({
  change: e => dispatch(changeSignal(e)),
});

export default connect(mapStateToProps, mapDispatchToProps)(TrafficSignal);
