import { createStore, combineReducers } from 'redux';
import { MOVE_CAR } from './actionCreators';
import { CHANGE_SIGNAL } from './actionCreators';

const initialStateCars = {
  cars: {
    red: false,
    blue: false,
    yellow: false,
  },
};

function reducerMoveCar(state = initialStateCars, action) {
  switch (action.type) {
    case MOVE_CAR:
      return { ...state, cars: { ...state.cars, [action.car]: action.side } };
  }
}

const initialStateChangeSignal = {
  signal: {
    color: 'red',
  },
};

function reducerChangeSignal(state = initialStateChangeSignal, action) {
  switch (action.type) {
    case CHANGE_SIGNAL:
      return { ...state, signal: { ...state.signal, color: action.payload } };
    default:
      return state;
  }
}

const reducersCombined = combineReducers({ moveCarReducer: reducerMoveCar, changeSignalReducer: reducerChangeSignal });

const store = createStore(reducersCombined, window.__REDUX_DEVTOOLS_EXTENSION__ && window.__REDUX_DEVTOOLS_EXTENSION__());

export default store;
