import { REQUEST_DATA , GET_DATA , FAILED_DATA } from '../actions';

initialState = {};

export const reducer = (state = initialState, action) => {
  switch(action.type) {
    case REQUEST_DATA:
      return Object.assign({}, state, { loading: true });
    case GET_DATA:
      return Object.assign({}, state, { loading: false, data: action.payload });
    case FAILED_DATA:
      return Object.assign({}, state, { loading: false, data: action.payload });
    default:
      return state;
  }
}
